Create Database sqldatagif



--link to datalake BLOB_STORAGE
CREATE EXTERNAL DATA SOURCE ExternalDataSourceDataLake
WITH(

    LOCATION = 'https://synapsedatalake55.dfs.core.windows.net/synpasecontainer/'
        );



--- CREATE A FILE FORMAT FOR WRITING DATA
CREATE EXTERNAL FILE FORMAT SynapseParquetFormat
WITH(

    FORMAT_TYPE = PARQUET
        );


-- SCHEMA FOR VIEWS AND EXTERNAL TABLES
CREATE SCHEMA LDW AUTHORIZATION dbo;
CREATE SCHEMA STG AUTHORIZATION dbo;


---SUPPORT FOR UTF FILES
ALTER DATABASE sqldatag COLLATE Latin1_General_100_BIN2_UTF8;


--security
CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'Solomongbu55';


CREATE DATABASE SCOPED CREDENTIAL WorkspaceIdentity
WITH IDENTITY = 'Managed Identity';
